package icehs.science.Chapter03;

public class AutoCastEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int small = 1000;
		long middle = small;
		double large = middle;
		
		System.out.println(small);
		System.out.println(middle);
		System.out.println(large);
	}

}
