package icehs.science.Chapter04;

public class CalculatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int firstNum = 20;
		int secondNum = 7;
		
		int plusResult = firstNum + secondNum;
		int minusResult = firstNum - secondNum;
		int multipleResult = firstNum * secondNum;
		int divisionResult = firstNum / secondNum;
		int remainerResult = firstNum % secondNum;
		
		System.out.println("오 써쎀 : " + plusResult);
		System.out.println("짋 써쎀 : " + minusResult);
		System.out.println("썼 써쎀 : " + multipleResult);
		System.out.println("씱얋 써쎀 : " + divisionResult);
		System.out.println("씱쟵쵔 : " + remainerResult);
	}

}
