package icehs.science.Chapter05;

public class SwitchEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		
		switch(x) {
		case 0 : System.out.println("0을 입력했습니다."); break;
		case 1 : System.out.println("1을 입력했습니다."); break;
		case 2 : System.out.println("2을 입력했습니다."); break;
		case 3 : System.out.println("3을 입력했습니다."); break;
		case 4 : System.out.println("4을 입력했습니다."); break;
		case 5 : System.out.println("5을 입력했습니다."); break;
		case 6 : System.out.println("6을 입력했습니다."); break;
		case 7 : System.out.println("7을 입력했습니다."); break;
		case 8 : System.out.println("8을 입력했습니다."); break;
		case 9 : System.out.println("9을 입력했습니다."); break;
		default : System.out.println("0~9를 벗어난 수를 입력했습니다.");
		}
	}

}
